@app.route('/CropYieldPredictor', methods=['POST'])
def PredictCropYield():
    data=request.json
    print(data)
    rainfall = data['rainfall']
    pesticides = data['pesticides']
    temperature = data['temperature']
    area = data['area']
    item = data['item']

    rainfall=int(rainfall)
    pesticides=int(pesticides)
    temperature=int(temperature)
    #print(rainfall)
    #print(pesticides)
    #print(temperature)
    #print(area)
    #print(item)
    
    input = []
    input.append(rainfall)
    input.append(pesticides)
    input.append(temperature)


    '''Code for crop selection start'''

    def addItem(item):
        if item == 'Maize':
            arr = [1, 0, 0, 0, 0, 0, 0, 0]
            for i in arr:
                input.append(i)

        elif item == 'Potatoes':
            arr = [0, 1, 0, 0, 0, 0, 0, 0]
            for i in arr:
                input.append(i)

        elif item == 'Rice':
            arr = [0, 0, 1, 0, 0, 0, 0, 0]
            for i in arr:
                input.append(i)

        elif item == 'Sorghum':
            arr = [0, 0, 0, 1, 0, 0, 0, 0]
            for i in arr:
                input.append(i)

        elif item == 'Soyabeans':
            arr = [0, 0, 0, 0, 1, 0, 0, 0]
            for i in arr:
                input.append(i)

        elif item == 'Wheat':
            arr = [0, 0, 0, 0, 0, 0, 1, 0]
            for i in arr:
                input.append(i)

        elif item == 'Sweet potatoes':
            arr = [0, 0, 0, 0, 0, 1, 0, 0]
            for i in arr:
                input.append(i)

        else:
            arr = [0, 0, 0, 0, 0, 0, 0, 1]
            for i in arr:
                input.append(i)

        return input

    def addCountry(area):
        arr=code_for_country[area]
        for i in arr:
            input.append(i)
        #print(arr)
        return input

    '''Code for area selection end'''

    input=addItem(item)
    input=addCountry(area)
    #print(input)


    """Loading Model and predicting yield"""
    with open('Model_RF', 'rb') as f:
        mp = pickle.load(f)

    prediction=mp.predict([input])
    ans=prediction[0]
    #print(ans)
    #print(input)
    return {"Yield":ans}